$(document).ready(function () {
    $("body").on("click", ".checkBtn button", function () {
      var name = $(this).data("title");
      var toggle = $(this).data("toggle");
      $(this)
        .parent()
        .next("." + toggle)
        .prop("value", name);
      $(this)
        .parent()
        .find('button[data-toggle="' + toggle + '"]')
        .not('[data-title="' + name + '"]')
        .removeClass("active")
        .addClass("notActive");
      $(this)
        .parent()
        .find('button[data-toggle="' + toggle + '"][data-title="' + name + '"]')
        .removeClass("notActive")
        .addClass("active");
    });
});